import React from 'react';
import { BrowserRouter, Route, Routes, Link, Outlet, useParams } from 'react-router-dom';




const Task = () => {
  const { taskId } = useParams();
  const task = mock.tarefas.find(task => task.id === taskId);
  if(!task) {
    return <div className='bg-green-200 p-4'>Tarefa nÃ£o encontrada</div>
  }
  switch(task.status) {
    case 'a fazer':
      return (
        <div className='bg-red-200 p-4 text-red-800 text-lg font-semibold mb-2'>
        <h3 className=''>{task.title}</h3>
        <p>{task.description}</p>
        <p>Status: {task.status}</p>
      </div>
      );
    case 'em andamento':
      return (
        <div className='bg-blue-200 p-4 text-blue-800 text-lg font-semibold mb-2'>
        <h3 className=''>{task.title}</h3>
        <p>{task.description}</p>
        <p>Status: {task.status}</p>
      </div>
      );
    case 'completa':
      return (
        <div className='bg-green-200 p-4 text-green-800 text-lg font-semibold mb-2'>
          <h3 className=''>{task.title}</h3>
          <p>{task.description}</p>
          <p>Status: {task.status}</p>
        </div>
        );
    default:
      return null;
  }
};

const UserSettings = () => (
  <div className='bg-yellow-100 p-4'>
    <h3 className='text-xl font-semibold mb-2'>ConfiguraÃ§Ãµes do UsuÃ¡rio</h3>
    <p>Idioma: PortuguÃªs</p>
    <p>NotificaÃ§Ãµes: Ativadas</p>
  </div>
);

function App() {
  return (
    <BrowserRouter>
      <nav className='flex justify-between bg-slate-800 text-white p-2'>
        <Link className='px-4 text-3xl font-bold hover:text-blue-500' to='/'>App Tarefas</Link>
        <Link className='bg-blue-500 hover:bg-blue-800 p-2 px-10 rounded' to='/profile'>Login</Link>
      </nav>
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/profile' element={<ProfileLayout />}>
          <Route index element={<UserInfo />} />
          <Route path='tasks' element={<UserTasks />}>
            <Route path=':taskId' element={<Task />} />
