import React from 'react';
import { BrowserRouter, Route, Routes, Link, Outlet } from 'react-router-dom';

const UserProfile = () => (
  <div className='p-10'>
    <h2 className='text-2xl font-semibold mb-4'>Perfil do Usuário</h2>
    <Outlet /> {/* Renderiza rotas aninhadas */}
  </div>
);

const UserInfo = () => (
  <div className='bg-blue-100 p-4'>
    <h3 className='text-xl font-semibold mb-2'>Informações Pessoais</h3>
    <p>Nome: John Doe</p>
    <p>Email: john@example.com</p>
  </div>
);

const UserPosts = () => (
  <div className='bg-green-100 p-4'>
    <h3 className='text-xl font-semibold mb-2'>Posts do Usuário</h3>
    <ul>
      <li>Post 1</li>
      <li>Post 2</li>
      <li>Post 3</li>
    </ul>
  </div>
);

const UserSettings = () => (
  <div className='bg-yellow-100 p-4'>
    <h3 className='text-xl font-semibold mb-2'>Configurações do Usuário</h3>
    <p>Idioma: Português</p>
    <p>Notificações: Ativadas</p>
  </div>
);

function App() {
  return (
    <BrowserRouter>
      <nav className='bg-slate-800 text-white p-4'>
        <Link className='px-4 text-3xl font-bold hover:text-blue-500' to='/'>App Rotas</Link>
        <Link className='bg-blue-500 hover:bg-blue-800 p-2 px-4 rounded' to='/perfil'>Perfil</Link>
      </nav>

      <Routes>
        <Route path='/' element={<div>Página Principal</div>} />
        <Route path='/perfil' element={<UserProfile />}>
          <Route index element={<UserInfo />} />
          <Route path='posts' element={<UserPosts />} />
          <Route path='configuracoes' element={<UserSettings />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
