import React from 'react';
import { BrowserRouter, Route, Routes, Link, Outlet, useParams } from 'react-router-dom';
import mock from './data/mock';





const UserInfo = () => (

  <div className='bg-blue-100 p-4'>
    <h3 className='text-xl font-semibold mb-2'>Informações Pessoais</h3>
    <p>Nome: John Doe</p>
    <p>Email: john@doe.com</p>
    <h3 className='text-xl font-semibold mb-2 mt-4'>Resumo do Usuário</h3>
      <p>A fazer: {mock.tarefas.filter(t => t.status === "a fazer").length}</p>  
      <p>Em andamento: {mock.tarefas.filter(t => t.status === "em andamento").length}</p>
      <p>Completa(s): {mock.tarefas.filter(t => t.status === "completa").length}</p>
  </div>
);

const UserTasks = () => {
  return (
    <div className='bg-green-100 p-4'>
      <h3 className='text-xl font-semibold mb-2'>Tarefas do Usuário</h3>
      <div className='grid grid-cols-3 gap-4'>
        {mock.tarefas.map(task => (
          <div key={task.id} className={"p-4 m-2 rounded-md flex flex-col gap-2" + (task.status === 'a fazer' ? ' bg-red-200 text-red-800' : task.status === 'em andamento' ? ' bg-blue-200 text-blue-800' : ' bg-green-200 text-green-800')
          }>
            <h3 className='text-lg font-semibold mb-2'>{task.title}</h3>
            <p>Status: {task.status}</p>
            <Link to={task.id.toString()} className='bg-blue-500 hover:bg-blue-800 font-semibold text-white p-2 px-10 rounded mt-2'>Ver Detalhes</Link>
          </div>
        ))}
      </div>
      <Outlet /> {/* Renderiza rotas aninhadas */}
    </div>
  )
};

const Task = () => {
  const { taskId } = useParams();
  const task = mock.tarefas.find(task => task.id === taskId);
  if(!task) {
    return <div className='bg-green-200 p-4'>Tarefa não encontrada</div>
  }
  switch(task.status) {
    case 'a fazer':
      return (
        <div className='bg-red-200 p-4 text-red-800 text-lg font-semibold mb-2'>
        <h3 className=''>{task.title}</h3>
        <p>{task.description}</p>
        <p>Status: {task.status}</p>
      </div>
      );
    case 'em andamento':
      return (
        <div className='bg-blue-200 p-4 text-blue-800 text-lg font-semibold mb-2'>
        <h3 className=''>{task.title}</h3>
        <p>{task.description}</p>
        <p>Status: {task.status}</p>
      </div>
      );
    case 'completa':
      return (
        <div className='bg-green-200 p-4 text-green-800 text-lg font-semibold mb-2'>
          <h3 className=''>{task.title}</h3>
          <p>{task.description}</p>
          <p>Status: {task.status}</p>
        </div>
        );
    default:
      return null;
  }
};

const UserSettings = () => (
  <div className='bg-yellow-100 p-4'>
    <h3 className='text-xl font-semibold mb-2'>Configurações do Usuário</h3>
    <p>Idioma: Português</p>
    <p>Notificações: Ativadas</p>
  </div>
);

function App() {
  return (
    <BrowserRouter>
      <nav className='flex justify-between bg-slate-800 text-white p-2'>
        <Link className='px-4 text-3xl font-bold hover:text-blue-500' to='/'>App Tarefas</Link>
        <Link className='bg-blue-500 hover:bg-blue-800 p-2 px-10 rounded' to='/profile'>Login</Link>
      </nav>
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/profile' element={<ProfileLayout />}>
          <Route index element={<UserInfo />} />
          <Route path='tasks' element={<UserTasks />}>
            <Route path=':taskId' element={<Task />} />
          </Route>
          <Route path='configuracoes' element={<UserSettings />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
