import React from 'react'



export default function Login({ user, setIsAuthenticated }) {

  const handleLogin = () => {
    // Simulação de autenticação bem-sucedida
    const username = document.getElementById('username').value;
    const password = document.getElementById('password').value;
    if (username !== 'admin' || password !== 'admin') {
      alert('Usuário ou senha inválidos!');
      return;
    } 
    setIsAuthenticated(true);
  };

  return (
    <>
    <h1 className='m-5 text-center font-bold text-blue-800 text-2xl'>Faça o login para a área administrativa</h1>
      <div className='flex font-bold  flex-col p-20 text-lg  bg-blue-200 text-center w-2/4 m-auto rounded-2xl'>
        <input id='username' className='border-2 border-blue-500 p-2' type="text" placeholder='Usuário' />
        <input id='password' className='border-2 border-blue-500 p-2' type="password" placeholder='Senha' />
        <button className='bg-blue-500 text-white hover:bg-blue-800 p-2 px-10' onClick={handleLogin}>Login</button>

      </div>
    </>
  )
}
