import React from 'react'



export default function Login({ setIsAuthenticated }) {

  const handleLogin = () => {
    // Simulação de autenticação bem-sucedida
    setIsAuthenticated(true);
  };

  return (
    <div className='flex flex-col p-20 text-lg  bg-blue-200 text-center w-2/4 m-auto rounded-2xl'>
      <h1 className='m-5 text-blue-800'>Login</h1>
      <input className='border-2 border-blue-500 p-2' type="text" placeholder='Usuário' />
      <input className='border-2 border-blue-500 p-2' type="password" placeholder='Senha' />
      <button className='bg-blue-500 hover:bg-blue-800 p-2 px-10' onClick={handleLogin}>Login</button>
    </div>
  )
}
